library(MCMCtreeR, quietly = TRUE, warn.conflicts = FALSE)
library(tidyr)
library(dplyr)
library(ggplot2)

# function to extract mean ages from a tree
extract_mean_ages <- function(tree, analysis) {
  
  # create the nexus-style object
  nexus_tree <- as.character(c("#NEXUS",
                               "begin trees",
                               "",
                               tree,
                               "END;"))
  
  # create mcmctree object
  complete_tree_mcmctree <- readMCMCtree(nexus_tree, from.file = FALSE)
  
  # extract age means
  age_means <- as.data.frame(complete_tree_mcmctree$nodeAges)["mean"]
  
  # rename column to match the analysis
  names(age_means) <- analysis 
  
  return(age_means)
  
}

# function to generate a correlation matrix
corr_matrix_ages <- function(trees) {
  
  run_names <- names(trees)
  
  # apply the function to each tree
  result_list <- lapply(run_names, function(i) {
    extract_mean_ages(trees[[i]], i)
  })
  
  # get the dataframe of mean values
  combined_results <- do.call(cbind, result_list)
  
  # create a correlation matrix
  correlation <- as.data.frame(cor(combined_results))
  
  correlation[lower.tri(correlation)] <- NA
  
  correlation <- correlation %>%
    tibble::rownames_to_column(var = "var1") %>%
    pivot_longer(cols = -1, names_to = "var2", values_to = "corr") %>%
    mutate(analysis = stringr::str_extract(var1, "^[^_]+"),
           across(c(var1, var2), ~ stringr::str_remove(., "^[^_]+_")))
  
  return(correlation)
  
}

