## LOAD AGE DATAFRAMES
# tidy up dataframes
allTree_ages <- tidy_age_df(all_tree$nodeAges, "allTree")
noBran_ages <- tidy_age_df(noBran_tree$nodeAges, "noBran")
noAnos_ages <- tidy_age_df(noAnos_tree$nodeAges, "noAnos")
noNoto_ages <- tidy_age_df(noNoto_tree$nodeAges, "noNoto")
noClad_ages <- tidy_age_df(noClad_tree$nodeAges, "noClad")

concGenes_ages <- tidy_age_df(concGenes_tree$nodeAges, "concGenes")
discGenes_ages <- tidy_age_df(discGenes_tree$nodeAges, "discGenes")
noLysp_ages <- tidy_age_df(noLysp_tree$nodeAges, "noLysp") %>%
  # adjust node labels to the absenc of Lysp
  mutate(Node = ifelse(Node <= 65, Node+1, Node+2))

## PLOTS JACK-KNIFE RESULTS
# merge age dataframes
df_list_jackknife <- list(allTree_ages,
                          noBran_ages,
                          noAnos_ages,
                          noNoto_ages,
                          noClad_ages)

# define order of panels
order_plots_jackknife <- c("noBran", "noAnos", "noNoto", "noClad")

# define strip labels
labeller_jackknife <- c('noAnos' = "B) No Anostraca calibration",
                        'noBran' = "A) No Branchiopoda calibration",
                        'noClad' = "D) No Cladocera calibration",
                        'noNoto' = "C) No Notostraca calibration")

# generate plot
plot_corr_jackknife <- plot_corr(df_list_jackknife, order_plots_jackknife, labeller_jackknife)
plot_corr_jackknife


## PLOTS SUBSETS RESULTS
# merge age dataframes
df_list_subsets <- list(allTree_ages,
                        concGenes_ages,
                        discGenes_ages,
                        noLysp_ages)

# define order of panels
order_plots_subsets <- c("noLysp", "concGenes", "discGenes")

# define strip labels
labeller_subsets <- c('noLysp' = "A) No Lynceus sp.",
                      'concGenes' = "B) Concordant genes",
                      'discGenes' = "C) Discordant genes")

# generate plot
plot_corr_subsets <- plot_corr(df_list_subsets, order_plots_subsets, labeller_subsets)
plot_corr_subsets


########################
#     SAVE RESULTS     #
########################

ggsave("plot_corr_jackknife.pdf",
       plot = plot_corr_jackknife, device = "pdf",
       dpi = 300, height = 8, width = 8, units = ("in"), bg = 'transparent')

ggsave("plot_corr_subsets.pdf",
       plot = plot_corr_subsets, device = "pdf",
       dpi = 300, height = 4, width = 12, units = ("in"), bg = 'transparent')

